import React from 'react'
import data from '../assets/products.json'

export const ProductList = (props) => {
  // TODO: display appropriate header
  // TODO: display only chosen columns
  // TODO: display currency column
  var columns = props.columns;
  return (
    <div id="product-list">
      <header>
        <strong>Product List ({data.length})</strong>
      </header>
      <table>
          {
            Object.keys(columns).map((column,index) =>{
              if(columns[column])
                return <>
                  <thead>
                    <tr>
                      <th key={index}>{column}</th>
                    </tr>
                  </thead>
                  <tbody>
                    {data.map((data, index) =>{
                      return <tr key= {index} >
                        <td>{data.id}</td>
                        <td>{data.name}</td>
                        <td>{data.department}</td>
                        <td>{data.price}</td>
                        <td>{data.currency ?? 'TBD'}</td>
                      </tr>
                    })}
                  </tbody>
                </>
            })
          }        
      </table>
    </div>
  )
}
