import React from 'react'
import data from '../assets/products.json'

export const ProductList = (props) => {
  // TODO: display appropriate header
  // TODO: display only chosen columns
  // TODO: display currency column
  var columns = props.columns;
  return (
    <div id="product-list">
      <header>
        <strong>Product List ({data.length})</strong>
      </header>
      <table>
        <thead>
          {
            Object.keys(columns).map((column,index) =>{
              return <th key={index} className="">{column}</th>
            })
          }
        </thead>
        <tbody>
          
        {data.map((data, index) =>{
          return <tr key= {index} >
            <td className=''>{data.id}</td>
            <td className=''>{data.name}</td>
            <td className=''>{data.department}</td>
            <td className=''>{data.price}</td>
            <td className=''>{data.currency ?? 'Undefined'}</td>
          </tr>
        })}
        </tbody>
      </table>
    </div>
  )
}
