import React from 'react'
import data from '../assets/products.json'

export const ProductList = (props) => {
  // TODO: display appropriate header
  // TODO: display only chosen columns
  // TODO: display currency column
  var columns = props.columns;

  

  return (
    <div id="product-list">
      <header>
        <strong>Product List ({data.length})</strong>
      </header>
      <table>
        <thead>
          <tr>
          {
            Object.entries(columns).map(([k,v]) =>(
              v && <th key={k} id={k} className="">{k}</th>
            ))
          }
          </tr>
        </thead>
        <tbody>
        {
          data.filter((product) => {
            if(props.price.priceFrom || props.price.priceTo){
              if(Number(props.price.priceFrom) < Number(product.price) || Number(props.price.priceTo) > Number(product.price)){
                return product
              }
            }else {
              return product
            }
          }
          )
          .map((product, index) => (
            <tr key={index} >
              {  Object.entries(columns).map(([k,v],index) =>(
                v && <td key={`data_${index}_${k}`}>{product[k] ?? 'TBD'}</td>
              ))}
            </tr>
          ))
        }
        </tbody>
      </table>
    </div>
  )
}
