import React from 'react'

export const ToggleColumns = (props) => {
  const onCheckboxClick = (e) => {
    props.columns[e] ? props.columns[e] = false : props.columns[e] = true 
    props.onCheckboxClick.name=props.columns[e].name
    props.onCheckboxClick.checked= props.columns[e];
  }

  

  // TODO: Bind handlers and props
  return (
    <div className="toggle-columns">
      { 
        Object.keys(props.columns).map((column, index) => {
          return ( 
          <div key={index}>
            <label htmlFor={column}>
              {column}
            </label>
            <input
              {...console.log(column , props?.columns?.[column])}
              defaultChecked={props.columns?.[column] ? true : false}
              id={column}
              name={column}
              onChange={() => {onCheckboxClick(column)}}
              type="checkbox" />
          </div>)
        })
      }
    </div>
  );
}
