import React from 'react'

export const ToggleColumns = (props) => {
  const onCheckboxClick = (e) => {
    props.columns[e] ? props.columns[e] = false : props.columns[e] = true 
    console.log(e , props.columns[e])
  }

  // TODO: Bind handlers and props
  return (
    <div className="toggle-columns">
      { 
        Object.keys(props.columns).map((column, index) => {
          return ( 
          <div key={index}>
            <label htmlFor={column}>
              {column}
            </label>
            <input
              checked={props?.columns?.[column]}
              id={column}
              name={column}
              onClick={() => {onCheckboxClick(column)}}
              type="checkbox" />
          </div>)
        })
      }
    </div>
  );
}
