import React from 'react'
import data from '../assets/products.json'

export const ProductList = (props) => {
  // TODO: display appropriate header
  // TODO: display only chosen columns
  // TODO: display currency column
  var columns = props.columns;
  console.log(columns)
  return (
    <div id="product-list">
      <header>
        <strong>Product List ({data.length})</strong>
      </header>
      <table>
        <thead>
          <tr>
          {
            Object.entries(columns).map(([k,v],index) =>(
              v && <th key={index} id={k} className="">{k}</th>
            ))
          }
          </tr>
        </thead>
        <tbody>
          


        {
          data.map((data, index) => (
            <tr key= {index} >
              {  Object.entries(columns).map(([k,v],index) =>(
                v && <td className={`${k}`} key={`${k}`}>{data[k]}</td>
              ))}
              <td className='id'>{data.id}</td>
              <td className='name'>{data.name}</td>
              <td className='department'>{data.department}</td>
              <td className='price'>{data.price}</td>
              <td className='currency'>{data.currency ?? 'Undefined'}</td>
            </tr>
          ))
        
        /*data.map((data, index) =>{
          return <tr key= {index} >
            <td className='id'>{data.id}</td>
            <td className='name'>{data.name}</td>
            <td className='department'>{data.department}</td>
            <td className='price'>{data.price}</td>
            <td className='currency'>{data.currency ?? 'Undefined'}</td>
          </tr>
        })*/}
        </tbody>
      </table>
    </div>
  )
}
